import React, { useState } from "react";
import { Eye, EyeOff } from "lucide-react";
import "./style.css";

export const TextInput = ({
  picture = null,
  disabled = false,
  readOnly = false,
  label = "",
  isPassword = false,
  isNumber = false,
}) => {
  const [showPassword, setShowPassword] = useState(false);
  const [inputValue, setInputValue] = useState("");
  const [validationMessage, setValidationMessage] = useState("");

  const togglePasswordVisibility = () => {
    setShowPassword((prev) => !prev);
  };

  // Validation logic
  const validateInput = (value) => {
    if (!value) {
      setValidationMessage("This field cannot be empty.");
    } else if (isNumber && !/^\d+$/.test(value)) {
      setValidationMessage("Please enter only numbers.");
    } else if (isPassword) {
      if (value.length < 6) {
        setValidationMessage("Password must be at least 6 characters.");
      } else if (value.length > 20) {
        setValidationMessage("Password cannot exceed 20 characters.");
      } else {
        setValidationMessage("");
      }
    } else {
      setValidationMessage("");
    }
  };

  const handleInputChange = (e) => {
    const value = e.target.value;
    setInputValue(value);
    validateInput(value);
  };

  return (
    <div className="text-input-container">
      <div className="text-input-wrapper">
        {picture && <span className="text-input-icon">{picture}</span>}
        <input
          type={
            isPassword && !showPassword
              ? "password"
              : isNumber
              ? "text"
              : "text"
          }
          className={`text-input-field ${picture ? "with-icon" : ""}`}
          placeholder={label}
          disabled={disabled}
          readOnly={readOnly}
          value={inputValue}
          onChange={handleInputChange}
          onBlur={() => validateInput(inputValue)}
        />
        {isPassword && (
          <span
            className="text-input-icon toggle-password"
            onClick={togglePasswordVisibility}
          >
            {showPassword ? <EyeOff size={18} /> : <Eye size={18} />}
          </span>
        )}
      </div>
      {validationMessage && (
        <p className="text-input-validation">{validationMessage}</p>
      )}
    </div>
  );
};
