import React, { useState } from "react";
import { Eye, EyeOff } from "lucide-react";
import "./style.css";

const TextInput = ({
  label,
  type = "standard",
  isDisabled = false,
  password = false,
  inputType = "text",
  readOnly = false,
  validation = "",
  icon = null,
  showPasswordToggle = false,
  className = "",
  ...props
}) => {
  const [showPassword, setShowPassword] = useState(false);

  const handleTogglePassword = () => {
    setShowPassword((prev) => !prev);
  };

  const inputClass =
    type === "filled"
      ? "text-input-filled"
      : type === "outlined"
      ? "text-input-outlined"
      : "text-input-standard";

  return (
    <div className={`text-input-container ${className}`}>
      {label && <label className="text-input-label">{label}</label>}
      <div className={`text-input-wrapper ${inputClass}`}>
        {icon && <span className="text-input-icon">{icon}</span>}
        <input
          type={password && !showPassword ? "password" : inputType}
          disabled={isDisabled}
          readOnly={readOnly}
          className="text-input-field"
          {...props}
        />
        {password && showPasswordToggle && (
          <button
            type="button"
            onClick={handleTogglePassword}
            className="text-input-toggle"
          >
            {showPassword ? <EyeOff size={18} /> : <Eye size={18} />}
          </button>
        )}
      </div>
      {validation && <p className="text-input-validation">{validation}</p>}
    </div>
  );
};

export default TextInput;
